import React, { useEffect, useState } from "react";

function Main() {
  const [data, setData] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % data.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [data]);

  useEffect(() => {
    const initialData = [
      "https://www.carnivalbkk.com/media/amasty/bannerslider/080201.jpg",
      "https://www.carnivalbkk.com/media/amasty/bannerslider/web_10702.jpg",
      "https://www.carnivalbkk.com/media/amasty/bannerslider/web_10167.jpg",
    ];
    setData(initialData);
  }, []);

  return (
    <div className=" relative top-20 z-[-1]">
      <div className="">
        <img className="" src={data[currentImageIndex]} alt="" />
      </div>
      <section className="mt-10 flex justify-center items-center">
        <div className=" text-center">
          <p>PRODUCT</p>
          <h1 className=" text-3xl">UPCOMING</h1>
        </div>
      </section>
      <section class="flex justify-center items-center">
  <div class="max-w-[1080px] overflow-x-auto">
    <div class="flex flex-wrap justify-center gap-8">
      <div class="w-56">
        <div class="flex justify-center items-center">
          
        </div>
        <div class="overflow-hidden text-ellipsis whitespace-nowrap text-center">
          <a href="#">PUMA x P.A.M. Contrast Tee PUMA Black (62407101)</a>
          <p>฿2,990.00</p>
        </div>
      </div>
     
    </div>
  </div>
</section>
      <section className="flex justify-center items-center mt-10 ">
        <button className="p-5 border border-black mb-4 hover:bg-black hover:text-white">สินค้าทั้งหมด</button>
      </section>
    </div>
  );
}

export default Main;
